import React, { useRef } from "react";
import { CommentPin } from "./CommentPin";
import type { Comment } from "@shared/schema";

type ImageWithCommentsProps = {
  src: string;
  alt: string;
  comments?: Comment[];
  onImageClick: (coords: { xPercent: number; yPercent: number }) => void;
  onPinClick: (commentId: string) => void;
  highlightedCommentId?: string | null;
};

export const ImageWithComments: React.FC<ImageWithCommentsProps> = ({
  src,
  alt,
  comments,
  onImageClick,
  onPinClick,
  highlightedCommentId,
}) => {
  const imgRef = useRef<HTMLImageElement>(null);

  // Filter root comments with positions and sort by creation date
  const rootComments = (comments || [])
    .filter(comment => !comment.parentId && comment.positionX !== null && comment.positionY !== null)
    .sort((a, b) => new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime());

  // Translate click position to % coordinates of image
  const handleClick = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    console.log('ðŸ–±ï¸ Image click event fired!', e);

    if (!imgRef.current) {
      console.log('âŒ No image ref found');
      return;
    }

    const rect = imgRef.current.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * 100;
    const y = ((e.clientY - rect.top) / rect.height) * 100;

    console.log('ðŸ“ Click position calculated:', { x, y, rect });

    onImageClick({ xPercent: x, yPercent: y });
  };

  return (
    <div className="relative w-full" style={{ position: "relative" }}>
      <img
        src={src}
        alt={alt}
        ref={imgRef}
        className="w-full rounded-lg"
        onClick={handleClick}
        style={{ cursor: "crosshair" }}
        draggable={false}
      />

      {/* Render all pins */}
      {rootComments.map((comment, idx) => (
        <CommentPin
          key={comment.id}
          number={idx + 1}
          xPercent={(comment.positionX || 0) / 100}
          yPercent={(comment.positionY || 0) / 100}
          onClick={() => onPinClick(comment.id)}
          comment={{
            content: comment.content,
            name: comment.name,
            tag: comment.tag
          }}
          isHighlighted={highlightedCommentId === comment.id}
        />
      ))}


    </div>
  );
};

client/src/components/ObjectUploader.tsx.content:
import { useState } from "react";
import type { ReactNode } from "react";
import Uppy from "@uppy/core";
import { DashboardModal } from "@uppy/react";
import "@uppy/core/dist/style.min.css";
import "@uppy/dashboard/dist/style.min.css";
import AwsS3 from "@uppy/aws-s3";
import type { UploadResult } from "@uppy/core";
import { Button } from "@/components/ui/button";

interface ObjectUploaderProps {
  maxNumberOfFiles?: number;
  maxFileSize?: number;
  onGetUploadParameters: () => Promise<{
    method: "PUT";
    url: string;
  }>;
  onComplete?: (
    result: UploadResult<Record<string, unknown>, Record<string, unknown>>
  ) => void;
  buttonClassName?: string;
  children: ReactNode;
}

/**
 * A file upload component that renders as a button and provides a modal interface for
 * file management.
 * 
 * Features:
 * - Renders as a customizable button that opens a file upload modal
 * - Provides a modal interface for:
 *   - File selection
 *   - File preview
 *   - Upload progress tracking
 *   - Upload status display
 * 
 * The component uses Uppy under the hood to handle all file upload functionality.
 * All file management features are automatically handled by the Uppy dashboard modal.
 */
export function ObjectUploader({
  maxNumberOfFiles = 1,
  maxFileSize = 10485760, // 10MB default
  onGetUploadParameters,
  onComplete,
  buttonClassName,
  children,
}: ObjectUploaderProps) {
  const [showModal, setShowModal] = useState(false);
  const [uppy] = useState(() =>
    new Uppy({
      restrictions: {
        maxNumberOfFiles,
        maxFileSize,
      },
      autoProceed: false,
    })
      .use(AwsS3, {
        shouldUseMultipart: false,
        getUploadParameters: onGetUploadParameters,
      })
      .on("complete", (result) => {
        onComplete?.(result);
      })
  );

  return (
    <div>
      <Button onClick={() => setShowModal(true)} className={buttonClassName}>
        {children}
      </Button>

      <DashboardModal
        uppy={uppy}
        open={showModal}
        onRequestClose={() => setShowModal(false)}
        proudlyDisplayPoweredByUppy={false}
      />
    </div>
  );
}

