          setModal({ ...modal, open });
        }}
        fileId={fileId}
        xPercent={modal.xPercent}
        yPercent={modal.yPercent}
      />
      {/* Render comment thread UI here if desired */}
    </div>
  );
};

client/src/components/ThreadedCommentLis.content:
import React, { useState } from "react";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Separator } from "@/components/ui/separator";
import { MessageCircle, Clock, Reply } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { buildCommentTree, type CommentWithReplies } from "@/lib/commentUtils";

import type { Comment } from "@shared/schema";

interface ThreadedCommentListProps {
  comments: Comment[];
  fileId: string;
  onCommentClick?: (commentId: string) => void;
  highlightedCommentId?: string;
  onReply?: (comment: Comment) => void;
}

export function ThreadedCommentList({ 
  comments, 
  fileId, 
  onCommentClick, 
  highlightedCommentId,
  onReply 
}: ThreadedCommentListProps) {

  // Build the comment tree from flat array
  const commentTree = buildCommentTree(comments);

  // Filter to only positioned root comments (comments with coordinates)
  const positionedRoots = commentTree.filter(
    comment => comment.positionX !== null && comment.positionY !== null
  );

  const getStatusColor = (tag: string) => {
    switch (tag) {
      case "To Do": return "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200";
      case "In Progress": return "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200";
      case "Resolved": return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200";
      default: return "bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-200";
    }
  };

  // Recursive render function for threaded comments
  const renderComment = (comment: CommentWithReplies, level: number = 0, pinNumber?: number): React.ReactNode => {
    const isHighlighted = highlightedCommentId === comment.id;
    const maxLevel = 4; // Limit visual nesting to prevent too much indentation
    const displayLevel = Math.min(level, maxLevel);

    // Different styling for different nesting levels
    const nestingStyles = [
      "", // Level 0 (root)
      "ml-4 border-l-2 border-blue-200 dark:border-blue-800 pl-3", // Level 1
      "ml-4 border-l-2 border-green-200 dark:border-green-800 pl-3", // Level 2
      "ml-4 border-l-2 border-purple-200 dark:border-purple-800 pl-3", // Level 3
      "ml-4 border-l-2 border-orange-200 dark:border-orange-800 pl-3", // Level 4+
    ];

    return (
      <div key={comment.id} className={level > 0 ? nestingStyles[displayLevel] : ""}>
        <div
          className={`p-3 border rounded-lg cursor-pointer transition-all duration-500 mb-3 ${
            isHighlighted 
              ? "bg-gradient-to-r from-orange-100 to-orange-50 dark:from-orange-900/30 dark:to-orange-800/20 border-2 border-orange-400 dark:border-orange-500 shadow-lg animate-pulse" 
              : "bg-gray-50 border-gray-200 hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700"
          }`}
          onClick={() => onCommentClick?.(comment.id)}
        >
          {/* Header with pin number (only for root comments), status, and timestamp */}
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-2">
              {level === 0 && pinNumber && (
                <div className="w-6 h-6 bg-blue-500 text-white rounded-full flex items-center justify-center text-xs font-medium">
                  {pinNumber}
                </div>
              )}
              <Badge variant="secondary" className={getStatusColor(comment.tag)}>
                {comment.tag}
              </Badge>
              {level > 0 && (
                <span className="text-xs text-gray-500 bg-gray-200 dark:bg-gray-700 px-2 py-1 rounded">
                  Reply
                </span>
              )}
            </div>
            <div className="flex items-center gap-1 text-xs text-gray-500">
              <Clock className="w-3 h-3" />
              {formatDistanceToNow(new Date(comment.createdAt), { addSuffix: true })}
            </div>
          </div>

          {/* Author */}
          <div className="flex items-center gap-2 mb-2">
            <Avatar className={level === 0 ? "w-6 h-6" : "w-5 h-5"}>
              <AvatarFallback className="text-xs">
                {comment.name.slice(0, 2).toUpperCase()}
              </AvatarFallback>
            </Avatar>
            <span className={`font-medium ${level === 0 ? "text-sm" : "text-xs"}`}>
              {comment.name}
            </span>
          </div>

          {/* Comment content */}
          <p className={`text-gray-700 dark:text-gray-300 mb-3 ${level === 0 ? "text-sm" : "text-xs"}`}>
            {comment.content}
          </p>

          {/* Reply button */}
          <Button
            variant="ghost"
            size="sm"
            className="text-xs text-gray-500 hover:text-gray-700 dark:hover:text-gray-300 px-2 py-1 h-auto"
            onClick={(e) => {
              e.stopPropagation();
              onReply?.(comment);
            }}
          >
            <Reply className="w-3 h-3 mr-1" />
            Reply
          </Button>
        </div>

        {/* Render replies recursively */}
        {comment.replies.length > 0 && (
          <div className="space-y-2">
            {comment.replies.map(reply => renderComment(reply, level + 1))}
          </div>
        )}
      </div>
    );
  };

  if (positionedRoots.length === 0) {
    return (
      <div className="text-center text-gray-500 py-8">
        <MessageCircle className="w-12 h-12 mx-auto mb-3 text-gray-300" />
        <p className="text-sm">No comments yet</p>
        <p className="text-xs text-gray-400 mt-1">Click on the image to add annotations</p>
      </div>
    );
  }

  return (
    <>
      <div className="space-y-4">
        {positionedRoots.map((comment, index) => 
          renderComment(comment, 0, index + 1)
        )}
      </div>

    </>
  );
}

client/src/components/VideoWithComments..content:
import { useState, useRef } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { MessageCircle, Play, Pause } from "lucide-react";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { CommentModal } from "./CommentModal";
import type { Comment, File } from "@shared/schema";

interface VideoWithCommentsProps {
  file: File;
  isPublic?: boolean;
}

export function VideoWithComments({ file, isPublic = false }: VideoWithCommentsProps) {
  const [showModal, setShowModal] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const videoRef = useRef<HTMLVideoElement>(null);

  const { data: comments = [], refetch } = useQuery<Comment[]>({
    queryKey: ["/api/files", file.id, "comments"],
    queryFn: async () => {
      const response = await fetch(`/api/files/${file.id}/comments`);
      if (!response.ok) throw new Error("Failed to fetch comments");
      return response.json();
    },
    enabled: isPublic,
  });

  const handleTimeUpdate = () => {
    if (videoRef.current) {
      setCurrentTime(videoRef.current.currentTime);
    }
  };

  const handlePlayPause = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleAddComment = () => {
    if (videoRef.current) {
      videoRef.current.pause();
      setIsPlaying(false);
      setShowModal(true);
    }
  };

  const jumpToTime = (timestamp: number) => {
    if (videoRef.current) {
      videoRef.current.currentTime = timestamp;
    }
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getTagColor = (tag: string) => {
    switch (tag) {
      case "To Do": return "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200";
      case "In Progress": return "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200";
      case "Resolved": return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200";
      default: return "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200";
    }
  };

  // Sort comments by timestamp
  const timestampComments = comments
    .filter(c => c.timestamp !== null && !c.parentId)
    .sort((a, b) => (a.timestamp || 0) - (b.timestamp || 0));

  const getReplies = (commentId: string) => comments.filter(c => c.parentId === commentId);

  return (
    <div className="space-y-4">
      <div className="relative">
        <video
          ref={videoRef}
          className="w-full rounded-lg"
          onTimeUpdate={handleTimeUpdate}
          onPlay={() => setIsPlaying(true)}
          onPause={() => setIsPlaying(false)}
          controls={!isPublic}
        >
          <source src={file.objectPath} type={file.mimeType} />
          Your browser does not support the video tag.
        </video>

        {isPublic && (
          <div className="absolute bottom-4 left-4 right-4 bg-black bg-opacity-75 rounded-lg p-3 text-white">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <Button
                  size="sm"
                  variant="secondary"
                  onClick={handlePlayPause}
                >
                  {isPlaying ? <Pause className="h-4 w-4" /> : <Play className="h-4 w-4" />}
                </Button>
                <span className="text-sm">
                  {formatTime(currentTime)}
                </span>
              </div>
              <Button
                size="sm"
                onClick={handleAddComment}
                className="flex items-center space-x-1"
              >
                <MessageCircle className="h-4 w-4" />
                <span>Add Comment</span>
              </Button>
            </div>
          </div>
        )}
      </div>

      {isPublic && timestampComments.length > 0 && (
        <Card>
          <CardContent className="p-4">
            <h4 className="font-semibold mb-3">Comments Timeline</h4>
            <ScrollArea className="h-64">
              <div className="space-y-3">
                {timestampComments.map((comment) => {
                  const replies = getReplies(comment.id);
                  return (
                    <div key={comment.id} className="border-b pb-3 last:border-b-0">
                      <div className="flex items-start justify-between mb-2">
                        <div className="flex items-center space-x-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => jumpToTime(comment.timestamp || 0)}
                            className="text-blue-600 hover:text-blue-800 p-0 h-auto font-mono"
                          >
                            {formatTime(comment.timestamp || 0)}
                          </Button>
                          <Badge className={getTagColor(comment.tag)}>
                            {comment.tag}
                          </Badge>
                        </div>
                      </div>
                      <div className="ml-2">
                        <p className="font-medium text-sm">{comment.name}</p>
                        <p className="text-sm mt-1">{comment.content}</p>
                        <p className="text-xs text-muted-foreground mt-1">
                          {new Date(comment.createdAt).toLocaleDateString()}
                        </p>

                        {replies.length > 0 && (
                          <div className="ml-4 mt-2 space-y-2 border-l-2 border-gray-200 pl-3">
                            {replies.map((reply) => (
                              <div key={reply.id}>
                                <p className="font-medium text-sm">{reply.name}</p>
                                <p className="text-sm">{reply.content}</p>
                                <p className="text-xs text-muted-foreground">
                                  {new Date(reply.createdAt).toLocaleDateString()}
                                </p>
                              </div>
                            ))}
                          </div>
                        )}
                      </div>
                    </div>
                  );
                })}
              </div>
            </ScrollArea>
          </CardContent>
        </Card>
      )}

      <CommentModal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        fileId={file.id}
        timestamp={Math.floor(currentTime)}
        onSuccess={() => refetch()}
      />
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

